import "./App.css";
import { useState, useRef } from "react";
// import Input from "./component/Input";

const CM_TO_FT = 30.48;

function App() {
  // const height = useRef();
  // const weight = useRef();

  const [height, setHeight] = useState();
  const [weight, setWeight] = useState();
  const [convert, setConvert] = useState(true);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
      setHeight(Math.round((height / CM_TO_FT) * 100) / 100);
      // height.current = Math.round((height.current / CM_TO_FT) * 100) / 100;
    } else {
      setConvert(true);
      setHeight(Math.round(height * CM_TO_FT));
      // height.current = Math.round(height.current * CM_TO_FT);
    }
  }

  function handleHeight(e) {
    let valueInput = +e.target.value;
    console.log(valueInput);

  }

  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      {/* <Input handleConvert={handleConvert} convert={convert} /> */}
      <form>
        <fieldset>
          <legend>Height</legend>
          {convert ? (
            <div>
              <input
                type="number"
                value={height}
                onChange={(e) => handleHeight(e)}
              />
              <label> Cm </label>
            </div>
          ) : (
            <div>
              <input
                type="number"
                value={height}
                onChange={(e) => handleHeight(e)}
              />
              <label> ft </label>
            </div>
          )}
        </fieldset>
        <button onClick={handleConvert}>
          {convert ? "Convert to Standard" : "Convert to Metric"}
        </button>
        <button>Clear</button>
        <fieldset>
          <legend>Weight</legend>
          {convert ? (
            <div>
              <input type="number" />
              <label> Kg </label>
            </div>
          ) : (
            <div>
              <input type="number" />
              <label> Pound </label>
            </div>
          )}
        </fieldset>
      </form>
    </div>
  );
}

export default App;
