import "./App.css";
import { useState, useRef } from "react";

const CM_TO_FT = 3.2808;
const FT_TO_INCH = 12;
const KG_TO_STONE = 0.157473044;
const STONE_TO_LB = 0.0714285714;

function App() {
  const height = useRef(0);
  const weight = useRef(0);
  const [convert, setConvert] = useState(true);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
    } else {
      setConvert(true);
    }
  }
  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      <form>
        <fieldset>
          <legend>Height</legend>
          {convert ? (
            <div>
              <input type="number" />
              <label> Cm</label>
            </div>
          ) : (
            <div>
              <input type="number" />
              <label> ft</label>
              <input type="number" />
              <label>inch</label>
            </div>
          )}
        </fieldset>
        <button onClick={handleConvert}>Change</button>
        <fieldset>
          <legend>Weight</legend>
          {convert ? (
            <div>
              <input type="number" />
              <label> Stone</label>
            </div>
          ) : (
            <div>
              <input type="number" />
              <label>ft</label>
              <input type="number" />
              <label> Pound</label>
            </div>
          )}
        </fieldset>
      </form>
    </div>
  );
}

export default App;
