import "./App.css";
import { useState, useRef } from "react";
// import Input from "./component/Input";

const CM_TO_FT = 30.48;

function App() {
  // const height = useRef();
  // const weight = useRef();

  const [height, setHeight] = useState(0);
  const [weight, setWeight] = useState(0);
  const [convert, setConvert] = useState(true);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
      setHeight(Math.round((height / CM_TO_FT) * 100) / 100);
      // height.current = Math.round((height.current / CM_TO_FT) * 100) / 100;
    } else {
      setConvert(true);
      setHeight(Math.round(height * CM_TO_FT));
      // height.current = Math.round(height.current * CM_TO_FT);
    }
  }

  function handleHeight(e) {
    setHeight(e.target.value);
  }

  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      {/* <Input handleConvert={handleConvert} convert={convert} /> */}
      <form>
        <fieldset>
          <legend>Height - {convert ? " Cm " : " Ft "}</legend>
          <input value={height} onChange={(e) => handleHeight(e)} />
        </fieldset>
        <fieldset>
          <legend>Weight - {convert ? " Kg " : " Pound "} </legend>
          <input value={height} onChange={(e) => handleHeight(e)} />
        </fieldset>
        <button onClick={handleConvert}>
          {convert ? "Convert to Standard" : "Convert to Metric"}
        </button>
      </form>
    </div>
  );
}

export default App;
