import "./App.css";
import { useState, useRef } from "react";
import Input from "./component/Input";

const CM_TO_INCHES = 2.54;
const KG_TO_LB = 2.2046226218;

function App() {
  const [height, setHeight] = useState(0);
  const [weight, setWeight] = useState(0);
  const [convert, setConvert] = useState(true);
  const [result, setResult] = useState(0);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
      setHeight((height / CM_TO_INCHES).toFixed(2));
      setWeight((weight * KG_TO_LB).toFixed(2));
    } else {
      setConvert(true);
      setHeight((height * CM_TO_INCHES).toFixed(2));
      setWeight((weight / KG_TO_LB));
    }
  }

  function handleHeight(e) {
    setHeight(e.target.value);
  }

  function handleWeight(e) {
    setWeight(e.target.value);
  }

  function handleCalculate(e) {
    e.preventDefault();
    if (convert) {
      let resultCalculate = (weight / height ** 2) * 10000;
      console.log(resultCalculate);
    } else {
      let resultCalculate = (weight / height ** 2) * 703;
      console.log(resultCalculate);
    }
  }

  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      <Input
        handleConvert={handleConvert}
        handleCalculate={handleCalculate}
        handleHeight={handleHeight}
        handleWeight={handleWeight}
        convert={convert}
        weight={weight}
        height={height}
      />
    </div>
  );
}

export default App;
