import "./App.css";
import { useState, useRef } from "react";
// import Input from "./component/Input";

const CM_TO_FT = 30.48;

function App() {
  const height = useRef();
  const weight = useRef();
  const [convert, setConvert] = useState(true);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
      height.current = Math.round((height.current / CM_TO_FT) * 100) / 100;
    } else {
      setConvert(true);
      height.current = Math.round(height.current * CM_TO_FT);
    }
  }

  function handleHeight(e) {
    let value = Number(e.target.value);
    if (!isNaN(value) && value >= 0) {
      height.current = value;
    }
  }

  function clearUnit() {
    height.current = 0;
  }

  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      {/* <Input handleConvert={handleConvert} convert={convert} /> */}
      <form>
        <fieldset>
          <legend>Height</legend>
          {convert ? (
            <div>
              <input
                type="number"
                value={height.current}
                onChange={(e) => handleHeight(e)}
              />
              <label> Cm </label>
            </div>
          ) : (
            <div>
              <input
                type="number"
                value={height.current}
                onChange={(e) => handleHeight(e.target.value)}
              />
              <label> ft </label>
            </div>
          )}
        </fieldset>
        <button onClick={handleConvert}>
          {convert ? "Convert to Standard" : "Convert to Metric"}
        </button>
        <button onClick={clearUnit}>Clear</button>
        <fieldset>
          <legend>Weight</legend>
          {convert ? (
            <div>
              <input type="number" />
              <label> Kg </label>
            </div>
          ) : (
            <div>
              <input type="number" />
              <label> Pound </label>
            </div>
          )}
        </fieldset>
      </form>
    </div>
  );
}

export default App;
