export default function Input({
  handleConvert,
  convert,
  handleWeight,
  handleHeight,
  height,
  weight,
  handleCalculate
}) {
  return (
    <form className='input-ctn'>
      <div>
        <fieldset>
          <legend>Height - {convert ? " cm " : " in "}</legend>
          <input value={height} onChange={(e) => handleHeight(e)} />
        </fieldset>
        <fieldset>
          <legend>Weight - {convert ? " kg " : " lb "} </legend>
          <input value={weight} onChange={(e) => handleWeight(e)} />
        </fieldset>
      </div>
      <button onClick={handleConvert}>
        {convert ? "Convert to Standard" : "Convert to Metric"}
      </button>
      <button onClick={handleCalculate}>
          Calculate
      </button>
    </form>
  );
}
