import "./App.css";
import { useState, useRef } from "react";
// import Input from "./component/Input";

function App() {
  const height = useRef(0);
  const weight = useRef(0);
  const [convert, setConvert] = useState(true);

  function handleConvert(e) {
    e.preventDefault();
    if (convert) {
      setConvert(false);
    } else {
      setConvert(true);
    }
  }

  function covertHeight(e, key) {
    let value = Number(e.target.value);
    if (key === "cm") {
      height.current = Math.round((value / 30.48) * 100) / 100;
    } else {
      height.current = Math.round(value * 30.48 * 100) / 100;
    }
  }

  return (
    <div className="App">
      <h1>Calculate Your Body Mass Index - BMI</h1>
      {/* <Input handleConvert={handleConvert} convert={convert} /> */}
      <form>
        <fieldset>
          <legend>Height</legend>
          {convert ? (
            <div>
              <input
                type="number"
                value={height.current}
                onChange={(e) => covertHeight(e, "cm")}
              />
              <label> Cm </label>
            </div>
          ) : (
            <div>
              <input
                type="number"
                value={height}
                onChange={(e) => covertHeight(e, "ft")}
              />
              <label> ft </label>
            </div>
          )}
        </fieldset>
        <button onClick={handleConvert}>
          {convert ? "Convert to Standard" : "Convert to Metric"}
        </button>
        <fieldset>
          <legend>Weight</legend>
          {convert ? (
            <div>
              <input type="number" />
              <label> Kg </label>
            </div>
          ) : (
            <div>
              <input type="number" />
              <label> Pound </label>
            </div>
          )}
        </fieldset>
      </form>
    </div>
  );
}

export default App;
